import React from "react";

const skills = [
  {
    name: "HTML",
    icon: "https://cdn-icons-png.flaticon.com/512/732/732212.png",
  },
  {
    name: "CSS",
    icon: "https://cdn-icons-png.flaticon.com/512/732/732190.png",
  },
 
  {
    name: "JavaScript",
    icon: "https://ih1.redbubble.net/image.318732690.4529/fcp,small,wall_texture,product,750x1000.u1.jpg",
  },
  {
    name: "TypeScript",
    icon: "data:image/png;base64,",
  },
  {
    name: "React",
    icon: "data:image/png;base64,",
  },
  {
    name: "Redux",
    icon: "data:image/png;base64,",
  },
  { name: "Tailwind CSS", icon: "data:image/png;base64," },
  { name: "Framer Motion", icon: "data:image/jpeg;base64," },
  { name: "SQL", icon: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQGGT_pnMHJdps9fGjcDaFSqcfFxO2E7BQk4g&s" },
];
const SkillsGrid = () => {
  return (
    <div className="p-6 bg-pink-50 m-2 border-2 rounded-xl">
      <h2 className="text-2xl md:text-3xl font-extrabold text-gray-800 text-center mb-4">My Skills</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-4">
        {skills.map((skill, index) => (
          <div
            key={index}
            className="relative bg-gray-200 rounded-lg shadow-md overflow-hidden w-24 h-24 mx-auto"
          >
            <div
              className="absolute inset-0 bg-cover bg-center"
              style={{ backgroundImage: `url(${skill.icon})` }}
            ></div>

            <div className="absolute bottom-0 w-full text-center bg-black/50 text-white py-1">
              <h3 className="text-xs font-semibold">{skill.name}</h3>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default SkillsGrid;
